def gen_dirs():
    start = [-1, -1]
    output = []
    while start[0] != 2:
        if any(start):
            output.append(tuple(start))

        start[1] += 1
        if start[1] == 2:
            start[1] = -1
            start[0] += 1

    return output

target = "XMAS"
dirs = gen_dirs()
xmas = xmas.splitlines()
explore = []
count = 0

for i in range(len(xmas)):
    for j in range(len(xmas[0])):
        if xmas[i][j] == 'X':
            for dir in dirs:
                explore.append((i, j, dir, 1))

while explore:
    i, j, dir, l = explore.pop()
    new_i, new_j = dir
    if l == 4:
        count += 1
        continue

    l += 1
    new_i += i
    new_j += j
    if new_i < 0 or new_i >= len(xmas) or new_j < 0 or new_j >= len(xmas[0]):
        continue

    if xmas[new_i][new_j] == target[l-1]:
        explore.append((new_i, new_j, dir, l))

print(count)
